from models.users import UserModelfrom schemas.users import UserSchemafrom utils.extensions import dbuser_schema = UserSchema()users_schema = UserSchema(many=True)def get_user_by_user_id(user_id):    return UserModel.query.filter(user_id == UserModel.user_id).first()def get_user_by_username(username):    return UserModel.query.filter(username == UserModel.username).first()def get_all_user():    return UserModel.query.filter().all()def db_commit():    return db.session.commit()def db_add(user):    return db.session.add(user)def db_delete(user):    return db.session.delete(user)